settings.cardCss:
.card {
   width: 100%;
   height: 100%;
   display: flex;
   flex-direction: column;
   box-sizing: border-box;
   align-items: center;
   justify-content: center;
   border: 1px gray solid;
}

.item {
    display: flex;
    flex-direction: column;
    flex: 0;
    height: 100%;
    width: 80%;
    align-items: center;
    justify-content: center;
    border: 1px gray solid;
    margin: 2rem;
    padding: 2rem 0rem;
    background-color: #e6e6e6;
    border-radius: 50%;
}

.info-row {
    display: flex;
    font-size: 2.2rem;
    padding: 1rem;
    align-items: center;
}

#pump-state, #tank-state {
    font-size: 2.2rem;
    display: flex;
    padding: 0rem 1rem;
    font-weight: 600;
}
