@import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@500&display=swap'); /* Clean futuristic font */

body {
    font-family: 'Times New Roman',Times New Roman;
    background-color: #fff; /* Dark background for contrast */
    margin: 0;
    height: 100vh; /* Take full viewport height */
    display: flex;
    justify-content: center; /* Center horizontally */
    align-items: center; /* Center vertically */
}

.card {

    justify-content: center; /* Center horizontally */
    align-items: center; /* Center vertically */
    background-color: #fff; /* Simplified dark card background */
    color: #ffffff;
    width: 100%;
    height: 50px; /* Adjusted height for the horizontal layout */
    padding-left: 50px;
    padding-right: 50px;
    border-radius: 16px; /* Slightly more rounded corners for a modern look */
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.3); /* Stronger shadow for depth */
}

.info-row {
    padding-top:15px;
    display: flex;
    justify-content: space-between;
    width: 100%; /* Occupy the full width of the card */
    align-items: center; /* Align the items in the center vertically */
    align-content: center;
    height: 50px;

}

.relay-title {
    max-width: 270px;
    font-size: 22px;
    font-weight: 600;
    color: #305680; 
    flex: 1; /* Ensure the title takes up one-third of the width */
    text-align: left;
}

.serial-number {
    font-weight: 600;
    font-size: 22px;
    color: #305680;
    flex: 1;
    text-align: center; /* Center the serial number */
}

.manufacturer-name {
    font-size: 22px;
    color: #305680;
    font-weight: 600;
    flex: 1;
    text-align: right; /* Align the manufacturer name to the right */
}

