@import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@500&display=swap'); /* Clean futuristic font */

body {
    font-family: 'Times New Roman',Times New Roman;
    background-color: #fff; /* Dark background for contrast */
    margin: 0;
    height: 50%; /* Take full viewport height */
    display: flex;
    justify-content: center; /* Center horizontally */
    align-items: center; /* Center vertically */
}

.card {
    justify-content: center; /* Center horizontally */
    align-items: center; /* Center vertically */
    background-color: #fff; /* Simplified dark card background */
    color: #ffffff;
    width: 100%;
    height: 50%; /* Adjusted height for the horizontal layout */
    padding-left: 50px;
    padding-right: 50px;
    border-radius: 16px; /* Slightly more rounded corners for a modern look */
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.3); /* Stronger shadow for depth */
}

.info-row {
    padding-top:15px;
    display: flex;
    justify-content: space-between;
    width: 100%; /* Occupy the full width of the card */
    align-items: center; /* Align the items in the center vertically */
    align-content: center;
    height: 50px;
}

.relay-title {
    font-size: 22px;
    font-weight: 600;
    color: #305680; 
    flex: 1; /* Ensure the title takes up one-third of the width */
    text-align: left;
}

.manufacturer-name {
    align-items: center;
    align-content: center;
    font-size: 22px;
    color: #305680;
    font-weight: 600;
    flex: 1;
    text-align: center; 
}

html, body {
    margin: 0;
    padding: 0;
    height: 100%;
}

/* Card container inside the widget */
.card {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: #fff;
    width: 100%;
    padding: 0px;
    box-sizing: border-box;
    overflow: auto;
    margin: 0px;
}

/* Centered trip condition */
.trip-condition {
    text-align: center;
    height: 75px;
}

/* Grid format for R, Y, B, E phases */
.info-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr); /* Two columns per row */
    column-gap: 5px;  /* Slight horizontal space between items */
    width: 100%;
    text-align: center;
    padding: 0;
    margin: 0;
}

.info-item {
    padding: 0px;
    margin: 0px;
    font-size: 10px;
}

.info-item p{
    padding: 0px;
    margin: 0px;
}

/* Centered time and date */
.time-date-row {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 100%;
    padding: 0px;
    margin: 0px;
}

.time-date-row p{
    margin: 0px;
    font-size: 25px;
}

