descriptor.templateCss:
@import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@500&display=swap'); /* Clean futuristic font */

body {
    font-family: 'Times New Roman',Times New Roman;
    background-color: #fff; /* Dark background for contrast */
    margin: 0;
    height: 100%; /* Take full viewport height */
    display: flex;
    justify-content: center; /* Center horizontally */
    align-items: center; /* Center vertically */
}

.card {
    display: flex;
    justify-content: center; /* Center horizontally */
    align-items: center; /* Center vertically */
    background-color: #fff; /* Simplified dark card background */
    color: #ffffff;
    width: 100%;
    height: 50px; /* Adjusted height for the horizontal layout */
    padding-left: 35px;
    padding-right: 35px;
    border-radius: 16px; /* Slightly more rounded corners for a modern look */
    box-shadow: 0 15px 30px rgba(0, 0, 0, 0.3); /* Stronger shadow for depth */
}

.info-row {
    padding:15px;
    display: flex;
    justify-content: space-between;
    width: 100%; /* Occupy the full width of the card */
    align-items: center; /* Align the items in the center vertically */
    align-content: center;
    height: 50px;

}

.relay-title {
    font-size: 22px;
    font-weight: 600;
    color: #305680; 
    flex: 1; /* Ensure the title takes up one-third of the width */
    text-align: left;
}

.serial-number {
    font-weight: 600;
    font-size: 22px;
    color: #305680;
    flex: 1;
    text-align: right;
}

.manufacturer-name {
    font-size: 22px;
    color: #305680;
    font-weight: 600;
    flex: 1;
    white-space: nowrap;
}

descriptor.controllerScript:
self.onInit = function() {
  self.$serialSpan = self.ctx.$container.find('.tb-serial-value');
};

self.onDataUpdated = function() {
  if (!self.$serialSpan) {
    console.error('Serial span not found');
    return;
  }

  let raw = '';
  if (self.ctx.data && self.ctx.data[0]) {
    const row = self.ctx.data[0].data.find(r => r[0] === 'serial_number');
    raw = row ? row[1] : '';
  } else if (self.ctx.settings && this.ctx.settings.serial_number) {
    raw = self.ctx.settings.serial_number;
  }

  let text = raw != null ? raw.toString() : '';
  if (/^\d+(\.\d+)?$/.test(text)) {
    text = Math.trunc(parseFloat(text)).toString();
  }

  self.$serialSpan.text(text);
};

